    private void btnConvertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConvertirActionPerformed
        boolean sw = true;
        int x = 0;
        
        if(txtValor.getText().isEmpty())
        {
            JOptionPane.showMessageDialog(null, "El campo está vacío", "Error", JOptionPane.WARNING_MESSAGE);
            sw = false;
        }
        
        if(sw)
        {
            try
            {
                x = Integer.parseInt(txtValor.getText());
            }
            catch(NumberFormatException e)
            {
                JOptionPane.showMessageDialog(null, "Valor introducido no válido", "Error", JOptionPane.ERROR_MESSAGE);
                sw = false;
            }
            
            if(sw)
            {
                String binario = "", reverse = "";
            
                if(x == 0)
                {
                    txtResultado.setText(String.valueOf(x));
                }
                else
                {
                    while (x != 0)
                    {
                        binario += x % 2;
                        x /= 2;
                    }
            
                    for (int i = binario.length() - 1; i >= 0; i--)
                    {
                        reverse += binario.charAt(i);
                    }
                
                    txtResultado.setText(reverse);
                }
            }
        }
    }//GEN-LAST:event_btnConvertirActionPerformed

